        (43, 'Instituto Federal de Telecomunicaciones', 'G', 'Regulación y supervisión', 9, 'Regulación para el desarrollo del nuevo ecosistema digital', 'Promover el desarrollo del ecosistema digital y la adopción de nuevas tecnologías y casos de uso digitales', '2022-01-01 19:03:03.0', 1, 24, 'Territorio', 1, 'Territorio', '5'),
        (43, 'Instituto Federal de Telecomunicaciones', 'G', 'Regulación y supervisión', 10, 'Regulación de los servicios de Telecomunicaciones y Radiodifusión y fortalecimiento de los derechos de sus usuarios y audiencias', 'Asegurar la calidad, diversidad y pluralidad de los servicios de telecomunicaciones y radiodifusión, y fortalecer los derechos de usuarios y audiencias en el ecosistema digital', '2022-01-01 19:10:07.0', 1, 24, 'Territorio', 1, 'Territorio', '5'),
