        (48, 'Cultura', 'S', 'Sujetos a Reglas de Operación', 57, 'Programas del Fondo Nacional de Fomento a las Artesanías (FONART)', 'Contribuir al bienestar social e igualdad, a través de fomentar, apoyar, promover, y difundir la actividad artesanal del país.', '2012-01-01 11:02:16.0', 610857, 1, 'Personas', 1, 'Personas', '1'),
        (48, 'Cultura', 'S', 'Sujetos a Reglas de Operación', 268, 'Programa de Apoyos a la Cultura', 'Desarrollar proyectos culturales comunitarios, artísticos y de infraestructura en el país, así como restauración en ciudades Mexicanas declaradas Patrimonio Mundial de la Humanidad.', '2017-01-01 14:13:16.0', 695000, 1, 'Personas', 1, 'Personas', '1'),
