        (50, 'Instituto Mexicano del Seguro Social', 'K', 'Proyectos de Inversión', 29, 'Programas de adquisiciones', 'Otorgar una atención de calidad a los usuarios por parte de las unidades médicas y no médicas del Instituto mediante la adquisición y provisión del equipamiento necesario.', '2010-01-01 21:01:20.0', 61253355, 1, 'Personas', 1, 'Personas', '5'),
        (50, 'Instituto Mexicano del Seguro Social', 'M', 'Apoyo al proceso presupuestario y para mejorar la eficiencia institucional', 1, 'Actividades de apoyo administrativo', 'Contribuir a garantizar la seguridad social de nuestros derechohabientes, mediante el uso eficiente de los recursos humanos, materiales y financieros de apoyo administrativo.', '2008-01-01 18:58:54.0', 79793, 22, 'Servidores públicos', 2, 'Servidores públicos de la propia dependencia', '5'),
